targets.expr:
sum( sum by (namespace, cluster) (
                  sum by (namespace, pod, cluster) (
                      max by (namespace, pod, container, cluster) (
                        kube_pod_container_resource_requests{resource="cpu",cluster="$cluster"}
                      ) * on(namespace, pod, cluster) group_left() max by (namespace, pod, cluster) (
                        kube_pod_status_phase {phase=~"Pending|Running", cluster="$cluster"} == 1
                      )
                  )
              )) / sum(kube_node_status_allocatable{resource="cpu",cluster="$cluster"})

sum( sum by (namespace, cluster) (
                  sum by (namespace, pod, cluster) (
                      max by (namespace, pod, container, cluster) (
                        kube_pod_container_resource_requests{resource="memory",cluster="$cluster"}
                      ) * on(namespace, pod, cluster) group_left() max by (namespace, pod, cluster) (
                        kube_pod_status_phase {phase=~"Pending|Running", cluster="$cluster"} == 1
                      )
                  )
              )) / sum(kube_node_status_allocatable{resource="memory",cluster="$cluster"})

sum(sum by (cluster, namespace, pod, container) (
                rate(container_cpu_usage_seconds_total{cluster="$cluster", image!=""}[5m])
              ) * on (cluster, namespace, pod) group_left(node) topk by (cluster, namespace, pod) (
                1, max by(cluster, namespace, pod, node) (kube_pod_info{node!="",cluster="$cluster"})
              )) by (namespace)

