sum(
   sum by (cluster, namespace, pod, container) (
          rate(container_cpu_usage_seconds_total{job="cadvisor", image!="", container!="POD",cluster="$cluster", namespace="$namespace"}[5m])
        ) * on (cluster, namespace, pod) group_left(node) topk by (cluster, namespace, pod) (
          1, max by(cluster, namespace, pod, node) (kube_pod_info{cluster="$cluster", namespace="$namespace"})
        )
* on(namespace,pod)
  group_left(owner_name, owner_kind) kube_pod_owner{cluster="$cluster", namespace="$namespace", owner_kind="$type"}
) by (owner_name, owner_kind)

